import {
    All_QUESTION_REQUEST,
    All_QUESTION_SUCCESS,
    All_QUESTION_FAIL,
    CLEAR_ERRORS
} from '../constants/questionConstants.js';

export const questionReducer = (state = { question: [] }, action) => {
    switch (action.type) {
        case All_QUESTION_REQUEST:
            return {
                loading: true,
                question: [],
        };
        case All_QUESTION_SUCCESS:
            return {
                loading: false,
                question: action.payload,
        };
        case All_QUESTION_FAIL:
            return {
                loading: false,
                error: action.payload,
        };

        case CLEAR_ERRORS:
            return {
                ...state,
                error: null,
        };

        default:
            return state
    }
};

export const deletequestionReducer = (state = {}, action) => {
    switch (action.type) {
      case DELETE_QUESTION_REQUEST:
        return {
          ...state,
          loading: true,
          isDeleted:false
        };
      case DELETE_QUESTION_SUCCESS:
        return {
          ...state,
          loading: false,
          isDeleted: action.payload,
        };
      case DELETE_QUESTION_FAIL:
        return {
          ...state,
          loading: false,
          error: action.payload,
        };
      case DELETE_QUESTION_RESET:
        return {
          ...state,
          isDeleted: false,
        };
      case CLEAR_ERRORS:
        return {
          ...state,
          error: null,
        };
      default:
        return state;
    }
  };
